import { React } from 'react'
import './App.css'

function App() {
	const [count, setCount] = React.useState(0)

	const onClickPlus = () => {
		setCount(setCount + 1)
	}

	const onClickMinus = () => {
		setCount(setCount - 1)
	}

	return (
		<div className='App'>
			<div>
				<h2>Счетчик:</h2>
				<h1>{count}</h1>
				<button className='plus'>+</button>
				<button className='minus'>-</button>
			</div>
		</div>
	)
}

export default App
